	class Widget
	{
		private:
			Image* backgroundImage;
		public:
			void click()
			{
				if (backgroundImage)
				{
					backgroundImage->click();
				}
			}
			void changeBackgroundImage(Image* newImage)
			{
				if (backgroundImage)
				{
					delete backgroundImage;
				}
				backgroundImage = newImage;
			}
	}
